import React from 'react';
import Axios from 'axios';

class AdminDashboard extends React.Component{
    constructor(props){
        super(props);
        this.state={name:"",error:1};
        Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
            if(res.status===200)
                Axios.get(`http://localhost:3002/cancreate/${res.data}`).then(res=>{
                    if(res.data==="admin"){
                        this.setState({name:"",error:0})
                    }
            })
        })
        if(!this.state.error){
            Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
                if(res.status===200){
                    Axios.get(`http://localhost:3002/name`,{header:{Authorization: `Bearer ${res.data}`}})
                    .then(res=>this.setState({name:res.data}))
                }
            })
        }
    }
   componentWillMount(){
        if(!this.state.error){
            Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
                if(res.status===200){
                    Axios.get(`http://localhost:3002/name`,{header:{Authorization: `Bearer ${res.data}`}})
                    .then(res=>this.setState({name:res.data}))
                }
            })
        }
    }
    render(){
        console.log(this.state.name)
        if(this.state.error){
        return(
            <div style={{marginTop:"7em",justifyContent:"center"}}>
                <h1 style={{textAlign:"center"}}>You are not an Admin</h1>
            </div>
        )
    }
    else{
         return(
                <div style={{marginTop:"7em",justifyContent:"center"}}>
                    <h1 style={{textAlign:"center"}}>Welcome home {this.state.name}</h1>
                </div>
            )
    }
    }
}
export default AdminDashboard;
