import React from 'react';
import ReactTable from 'react-table';
import checkboxHOC from "react-table/lib/hoc/selectTable";
import "react-table/react-table.css";
import Axios from 'axios';

const CheckboxTable=new checkboxHOC(ReactTable);

class AssigningDeveloper extends React.Component{
    constructor(props){
        super(props);
        this.state={
          data:[],
          selection:[],
          selectAll:false
        };
        this.fetchData=this.fetchData.bind(this);
        this.developer=React.createRef();
        this.clicked=this.clicked.bind(this);
    }
    fetchData(){
        Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
            if(res.status===200){
                Axios.get('http://localhost:3002/get_all_developers',{headers:{Authorization: `Bearer ${res.data}`}})
                .then(res=>{
                    this.setState({data:res.data});
                })
            }
        })
    }
    clicked(e){
        console.log(e);
    }
    render() {
      const { toggleSelection, toggleAll, isSelected, logSelection } = this;
      const { data, columns, selectAll } = this.state;


      const checkboxProps = {
        selectAll,
        isSelected,
        toggleSelection,
        toggleAll,
        selectType: "checkbox",
        getTrProps: (s, r) => {
          // someone asked for an example of a background color change
          // here it is...
          const selected = this.isSelected(r.original._id);
          return {
            style: {
              backgroundColor: selected ? "lightgreen" : "inherit"
              // color: selected ? 'white' : 'inherit',
            }
          };
        }
      };


        return (
          <div style={{width:"80%",margin:"5em 15%"}}>
          <h1>{this.props.match.params.id}</h1>
            <CheckboxTable style={{textAlign:"center"}}
                ref={this.developer}
                onClick={this.clicked(this.developer)}
              columns={[
                {
                  Header: "Name",
                  accessor:"name",
                  width:130
                },
                {
                  Header: "ContactNo.",
                  accessor:"contactNo",
                  width:100
                },
                {
                  Header: 'Skills',
                  accessor:"skills"
                }
              ]}
              data={data}
              onFetchData={this.fetchData}
            />
            <br />
          </div>
        );
      }
}
export default AssigningDeveloper;
