import React from 'react';
import ReactTable from 'react-table';
import checkboxHOC from "react-table/lib/hoc/selectTable";
import "react-table/react-table.css";
import Axios from 'axios';

const CheckboxTable=new checkboxHOC(ReactTable);

class AssigningDeveloper extends React.Component{
    constructor(props){
        super(props);
        this.state={
          data:[],
          developers:[],
          selection:[],
          selectAll:false
        };
        this.fetchData=this.fetchData.bind(this);

    }
    fetchData(){
        Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
            if(res.status===200){
                Axios.get(`http://localhost:3002/get_all_developers`,{headers:{Authorization: `Bearer ${res.data}`}})
                .then(res=>{
                    this.setState({data:res.data});
                })
                Axios.get(`http://localhost:3002/getdeveloperinproject/${this.props.match.params.proid}`).then(res=>{
                  if(res.status===200)
                    this.setState({developers:res.data.developers});
                })
                const activedeveloper=this.state.data.developers.filter(i=>{
                  return this.state.developers.map(j=>{
                    if(j._id===i.devid)
                      return i;
                  })
                })
                console.log(activedeveloper);
            }
        })
    }


    toggleSelection = (key, shift, row) => {
      let selection = [...this.state.selection];
      const keyIndex = selection.indexOf(key);
      if (keyIndex >= 0) {
        selection = [
          ...selection.slice(0, keyIndex),
          ...selection.slice(keyIndex + 1)
        ];
      } else {
        selection.push(key);
      }
      this.setState({ selection });
    };

    toggleAll = () => {

      const selectAll = this.state.selectAll ? false : true;
      const selection = [];
      if (selectAll) {
        const wrappedInstance = this.checkboxTable.getWrappedInstance();
        const currentRecords = wrappedInstance.getResolvedState().sortedData;
        currentRecords.forEach(item => {
          selection.push(item._original._id);
        });
      }
      this.setState({ selectAll, selection });
    };

    isSelected = key => {

      return this.state.selection.includes(key);
    };

    logSelection = () => {
      Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
        if(res.status===200){
          Axios.put(`http://localhost:3002/assigndevelopers/${this.props.match.params.id}`,{developers:this.state.selection},{headers:{Authorization: `Bearer ${res.data}`}})
        }
      })
    };


    render() {
      const { toggleSelection, toggleAll, isSelected, logSelection } = this;
      const { data , selectAll } = this.state;


      const checkboxProps = {
        selectAll,
        isSelected,
        toggleSelection,
        toggleAll,
        selectType: "checkbox",
      };


        return (
          <div style={{width:"80%",margin:"5em 15%"}}>
          <h1>{this.props.match.params.id}</h1>
          <button onClick={logSelection}>Log Selection</button>
            <CheckboxTable style={{textAlign:"center"}}
                ref={r => this.checkboxTable = r}
              columns={[
                {
                  Header: "Name",
                  accessor:"name",
                  width:130
                },
                {
                  Header: "ContactNo.",
                  accessor:"contactNo",
                  width:100
                },
                {
                  Header: 'Skills',
                  accessor:"skills"
                }
              ]}
              data={data}
              onFetchData={this.fetchData}
              {...checkboxProps}
            />
            <br />
          </div>
        );
      }
}
export default AssigningDeveloper;
