import React from 'react';
import Axios from 'axios';

class ProjectforDev extends React.Component{
    constructor(props){
        super(props);
        this.state={data:null};
        this.update=this.update.bind(this);
        this.edit=this.edit.bind(this);
        this.input=React.createRef();
        Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
            if(res.status===200){
                Axios.get(`http://localhost:3002/getstatus/${this.props.proid}`,{headers:{Authorization: `Bearer ${res.data}`}})
                .then(res=>{
                    this.setState({data:res.data})
                })
            }
        })

    }

    update(){
        console.log("yes");
    }
    edit(e){
        e.target.readOnly=false;
    }

    render(){
        return(
            <div style={{marginTop:'7em'}}>
                <label>Status</label>
                <input type="text" value={this.state.data} onClick={this.edit} readOnly/>
                <button onClick={this.update}>Set Status</button>
            </div>
        )
    }
}
export default ProjectforDev;
