import React from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import Axios from 'axios';

class ShowTable extends React.Component{

    constructor(props){
        super(props);
        this.state={data:[]};
        this.fetchdata=this.fetchdata.bind(this);
        this.open=this.open.bind(this);
    }

    open(){
        console.log("yes");
    }

    componentWillReceiveProps(){
        this.state.data=[];
       this.fetchdata();
    }

    fetchdata(){
        Axios.get('http://localhost:3002/user',{withCredentials:true}).then(res=>{
            if(res.status===200){
                Axios.get(`http://localhost:3002/project/${this.props.type}`,{headers:{Authorization: `Bearer ${res.data}`}}).then(res=>{
                    if(res.data.length!==0)
                        this.setState({data:res.data})
                })
            }
        })
    }

    render(){
        return(
       <div style={{display:"center"}}>
       <button onClick={this.props.selected}>S</button>
             <ReactTable
            ref={ref=>this.selected=ref}
          columns={[
            {
              Header: "Name",
              accessor: "name"
            }
          ]}
          data={this.state.data}
          onFetchData={this.fetchdata}
          filterable
          className="-striped -highlight"
        />
       </div>
        )
    }
}
export default ShowTable;
